<script>
    import DemoFranchisor from "../../../classes/DemoFranchisor";
    import FormatsNumbers from "../../../mixins/FormatsNumbers";

    export default {
        mixins: [FormatsNumbers],

        props: {

        },

        data() {
            return {
                fields: [
                    'date',
                    'office',
                    {
                        key: 'total_amount',
                        formatter: val => this.moneyFormat(val),
                    },
                    'action'
                ],
                franchiseCount: DemoFranchisor.getFranchises().length,
                items: [],
            }
        },

        mounted() {
            this.items = DemoFranchisor.getPayments().slice(0, this.franchiseCount);
        },

        computed: {
            sum() {
                return this.items.reduce((carry, item) => carry + parseInt(item.total_amount), 0);
            }
        },

